import React, { Component } from "react";
import {
  View,
  StyleSheet,
  TextInput,
  TouchableOpacity,
  Text,
  ImageBackground,
  Image,
  Alert,
  KeyboardAvoidingView,
  ToastAndroid
} from "react-native";
import * as Permissions from "expo-permissions";
import { BarCodeScanner } from "expo-barcode-scanner";
import firebase from "firebase";
import db from "../config";

const bgImage = require("../assets/background2.png");
const appIcon = require("../assets/appIcon.png");

export default class RideScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bikeId: "",
      userId: "",
      domState: "normal",
      hasCameraPermissions: null,
      scanned: false,
      bikeType: "",
      userName: ""
    };
  }

  getCameraPermissions = async () => {
    const { status } = await Permissions.askAsync(Permissions.CAMERA);

    this.setState({
      /*status === "granted" es "true" cuando el usuario ha dado permiso
          status === "granted" es "false" cuando el usuario no ha dado permiso
        */
      hasCameraPermissions: status === "granted",
      domState: "scanner",
      scanned: false
    });
  };

  handleBarCodeScanned = async ({ type, data }) => {
    this.setState({
      bikeId: data,
      domState: "normal",
      scanned: true
    });
  };

  handleTransaction = async () => {
    var { bikeId, userId } = this.state;
    await this.getBikeDetails(bikeId);
    await this.getUserDetails(userId);

    var transactionType = await this.checkBikeAvailability(bikeId);

    if (!transactionType) {
      this.setState({ bikeId: "" });
      Alert.alert("Por favor escanea/ingresa un id válido de bicicleta");
    } else if (transactionType === "under_maintenance") {
      this.setState({
        bikeId: ""
      });
    } else if (transactionType === "rented") {
      var isEligible = await this.checkUserEligibilityForStartRide(userId);

      if (isEligible) {
        var { bikeType, userName } = this.state;
        this.assignBike(bikeId, userId, bikeType, userName);
        Alert.alert(
          "Has rentado la bicicleta por la siguiente 1 hora. ¡Disfruta tu viaje!"
        );
        this.setState({
          bikeAssigned: true
        });

        // Solo para usuarios Android
        // ToastAndroid.show(
        //   "Has rentado la bicicleta por la siguiente 1 hora. ¡Disfruta tu viaje!",
        //   ToastAndroid.SHORT
        // );
      }
    } else {
      var isEligible = await this.checkUserEligibilityForEndRide(
        bikeId,
        userId
      );

      if (isEligible) {
        var { bikeType, userName } = this.state;
        this.returnBike(bikeId, userId, bikeType, userName);
        Alert.alert("Esperamos que hayas disfrutado tu viaje");
        this.setState({
          bikeAssigned: false
        });

        // Solo para usuarios Android
        // ToastAndroid.show(
        //   "Esperamos que hayas disfrutado tu viaje",
        //   ToastAndroid.SHORT
        // );
      }
    }
  };

  getBikeDetails = bikeId => {
    bikeId = bikeId.trim();
    db.collection("bicycles")
      .where("id", "==", bikeId)
      .get()
      .then(snapshot => {
        snapshot.docs.map(doc => {
          this.setState({
            bikeType: doc.data().bike_type
          });
        });
      });
  };

  getUserDetails = userId => {
    db.collection("users")
      .where("id", "==", userId)
      .get()
      .then(snapshot => {
        snapshot.docs.map(doc => {

          // this.setState(
          //   userName: doc.data().name,
          //   userId: doc.data().id,
          //   bikeAssigned: doc.data().bike_assigned
          // );

          // this.setState({
          //   userName= doc.data().name,
          //   userId= doc.data().id,
          //   bikeAssigned= doc.data().bike_assigned
          // });

          // this.setState({
          //   userName: doc.data().name
          //   userId: doc.data().id
          //   bikeAssigned: doc.data().bike_assigned
          // });

           this.setState({
             userName: doc.data().name,
             userId: doc.data().id,
             bikeAssigned: doc.data().bike_assigned
           });


        });
      });
  };

  checkBikeAvailability = async bikeId => {
    const bikeRef = await db
      .collection("bicycles")
      .where("id", "==", bikeId)
      .get();

    var transactionType = "";
    if (bikeRef.docs.length == 0) {
      transactionType = false;
    } else {
      bikeRef.docs.map(doc => {
        if (!doc.data().under_maintenance) {
          //si la bicicleta está disponible entonces el tipo de transacción será rented de otro modo será return

       transactionType = doc.data().is_bike_available ?
        "rented" : "return";

        /*transactionType = doc.data().is_bike_available :
        "rented" ? "return";*/

        /*transactionType === doc.data().is_bike_available ?
        "rented" : "return";*/

        /*transactionType = doc.data().is_bike_available 
        "rented"  "return";*/


        } else {
          transactionType = "under_maintenance";
          Alert.alert(doc.data().maintenance_message);
        }
      });
    }

    return transactionType;
  };

  checkUserEligibilityForStartRide = async userId => {
    const userRef = await db
      .collection("users")
      .where("id", "==", userId)
      .get();

    var isUserEligible = false;
    if (userRef.docs.length == 0) {
      this.setState({
        bikeId: ""
      });
      isUserEligible = false;
      Alert.alert("ID de usuario onválido");
    } else {
      userRef.docs.map(doc => {
        if (!doc.data().bike_assigned) {
          isUserEligible = true;
        } else {
          isUserEligible = false;
          Alert.alert("Finaliza el viaje actual para rentar otra bicicleta.");
          this.setState({
            bikeId: ""
          });
        }
      });
    }

    return isUserEligible;
  };

  checkUserEligibilityForEndRide = async (bikeId, userId) => {
    const transactionRef = await db
      .collection("transactions")
      .where("bike_id", "==", bikeId)
      .limit(1)
      .get();
    var isUserEligible = "";
    transactionRef.docs.map(doc => {
      var lastBikeTransaction = doc.data();
      if (lastBikeTransaction.user_id === userId) {
        isUserEligible = true;
      } else {
        isUserEligible = false;
        Alert.alert("La bicicleta está rentada por otro usuario");
        this.setState({
          bikeId: ""
        });
      }
    });
    return isUserEligible;
  };

  assignBike = async (bikeId, userId, bikeType, userName) => {
    //agrega una transacción
    db.collection("transactions").add({
      user_id: userId,
      user_name: userName,
      bike_id: bikeId,
      bike_type: bikeType,
      date: firebase.firestore.Timestamp.now().toDate(),
      transaction_type: "rented"
    });
    //cambia el estatus de la bicicleta
    db.collection("bicycles")
      .doc(bikeId)
      .update({
        is_bike_available: false
      });
    //cambia el valor de la bicicleta asignada para el usuario
    db.collection("users")
      .doc(userId)
      .update({
        bike_assigned: true
      });

    // Actualizar estado local
    this.setState({
      bikeId: ""
    });
  };

  returnBike = async (bikeId, userId, bikeType, userName) => {
    //agregar una transacción
    db.collection("transactions").add({
      user_id: userId,
      user_name: userName,
      bike_id: bikeId,
      bike_type: bikeType,
      date: firebase.firestore.Timestamp.now().toDate(),
      transaction_type: "return"
    });
    //cambiar el estatus de la bicicleta
    db.collection("bicycles")
      .doc(bikeId)
      .update({
        is_bike_available: true
      });
    //cambiar el valor de la bicicleta asignada por el usuario
    db.collection("users")
      .doc(userId)
      .update({
        bike_assigned: false
      });

    // Actualizar estado local
    this.setState({
      bikeId: ""
    });
  };

  render() {
    const { bikeId, userId, domState, scanned, bikeAssigned } = this.state;
    if (domState !== "normal") {
      return (
        <BarCodeScanner
          onBarCodeScanned={scanned ? undefined : this.handleBarCodeScanned}
          style={StyleSheet.absoluteFillObject}
        />
      );
    }
    return (
      <KeyboardAvoidingView behavior="padding" style={styles.container}>
        <View style={styles.upperContainer}>
          <Image source={appIcon} style={styles.appIcon} />
          <Text style={styles.title}>Travesía digital</Text>
          <Text style={styles.subtitle}>Un viaje ecológico</Text>
        </View>
        <View style={styles.lowerContainer}>
          <View style={styles.textinputContainer}>
            <TextInput
              style={[styles.textinput, { width: "82%" }]}
              onChangeText={text => this.setState({ userId: text })}
              placeholder={"Id del usuario"}
              placeholderTextColor={"#FFFFFF"}
              value={userId}
            />
          </View>
          <View style={[styles.textinputContainer, { marginTop: 25 }]}>
            <TextInput
              style={styles.textinput}
              onChangeText={text => this.setState({ bikeId: text })}
              placeholder={"Id de la bicicleta"}
              placeholderTextColor={"#FFFFFF"}
              value={bikeId}
              autoFocus
            />
            <TouchableOpacity
              style={styles.scanbutton}
              onPress={() => this.getCameraPermissions()}
            >
              <Text style={styles.scanbuttonText}>Escanear</Text>
            </TouchableOpacity>
          </View>

          <TouchableOpacity
            style={[styles.button, { marginTop: 25 }]}
            onPress={this.handleTransaction}
          >
            <Text style={styles.buttonText}>
              {bikeAssigned ? "Fin del viaje" : "Desbloquear"}
            </Text>
          </TouchableOpacity>
        </View>
      </KeyboardAvoidingView>
    );
  }
}

